    public function getSourceContext()
    {
        return new Source("{% extends 'back&front/back.html.twig' %}

{% block title %}Edit Restaurant{% endblock %}

{% block body %}
<div class=\"container\">
    <div class=\"row justify-content-center align-items-center\" style=\"height: 100vh;\">
        <div class=\"col-md-6\">
            <h1 class=\"text-center mb-4\">Edit Restaurant</h1>

            {{ form_start(form, {'attr': {'novalidate': 'novalidate'}}) }}

            {# Display error messages if any #}
            {% if form_errors(form) %}
                <div class=\"alert alert-danger\">
                    <ul>
                        {% for error in form_errors(form) %}
                            <li>{{ error.message }}</li>
                        {% endfor %}
                    </ul>
                </div>
            {% endif %}

            {{ form_row(form.nomRestaurant, {
                'attr': {
                    'oninvalid': \"setCustomValidity('Restaurant name cannot contain numbers.')\",
                    'class': 'form-control'
                }
            }) }}

            {{ form_row(form.adresseRestaurant, { 'attr': { 'class': 'form-control' } }) }}

            {{ form_row(form.restaurantImage, { 'attr': { 'class': 'form-control' } }) }}

            {{ form_row(form.restaurantCategory, { 'attr': { 'class': 'form-select' } }) }}

            <div class=\"d-grid gap-2 mt-4\">
                <button type=\"submit\" class=\"btn btn-primary\">Update</button>
                <a href=\"{{ path('app_restaurant_index') }}\" class=\"btn btn-secondary mt-3\">Back to list</a>
            </div>

            {{ form_end(form) }}
        </div>
    </div>
</div>
{% endblock %}
", "Admin/restaurant/edit.html.twig", "C:\\Users\\legion 5\\Desktop\\userprojet\\templates\\Admin\\restaurant\\edit.html.twig");
