        .container {
            max-width: 800px;
            margin: 50px auto;
            padding: 20px;
            background-color: #fff;
            border-radius: 10px;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
        }

        /* Heading styling */
        h1 {
            color: #333;
            text-align: center;
            margin-bottom: 30px;
        }

        /* Form styling */
        .form-wrapper {
            margin-bottom: 20px;
        }

        /* Update button styling */
        .update-btn {
            background-color: #007bff; /* Blue */
            border: 1px solid #007bff; /* Blue border */
            color: #fff; /* White text */
            padding: 12px 24px;
            border-radius: 5px;
            cursor: pointer;
            transition: background-color 0.3s ease;
        }

        .update-btn:hover {
            background-color: #0056b3; /* Darker blue color on hover */
